                            if (pistol == 0)
                            {
                                pistol = rand.Next(0, PistolComboBox.Items.Count - 1);
                            }
                            else
                            {
                                pistol--;
                            }

                            byte[] intBytes = BitConverter.GetBytes(pistol);
                            if (!BitConverter.IsLittleEndian)
                                Array.Reverse(intBytes);
                            byte[] result = intBytes;
                            stream.Write(result, 0, result.Length);
                            stream.Flush();
                        }
                    }

                    rpkgFilesPatched += file + "\n";

                    rpkgFilesPatchedCount++;
                }
                else
                {
                    rpkgFilesNotPatched += file + "\n";
                }
            }

            if (rpkgFilesPatchedCount == 0)
            {
                string message = "Error: Could not locate any of the All NPCs Have Guns mod's RPKG Files listed below:\n";

                foreach (string file in rpkgOffsets.Keys)
                {
                    message += file + "\n";
                }

                message += "\nMake sure this executable is located in your Hitman 3 Runtime directory!";

                MessageBoxShow(message);
            }
            else if (rpkgFilesPatchedCount == rpkgOffsets.Keys.Count)
            {
                string message = "All of the All NPCs Have Guns mod's RPKG Files listed below were found/patched:\n";

                message += rpkgFilesPatched;

                MessageBoxShow(message);
            }
            else
            {
                string message = "The All NPCs Have Guns mod's RPKG Files listed below were found/patched:\n";

                message += rpkgFilesPatched;

                message += "\nThe All NPCs Have Guns mod's RPKG Files listed below were not found/patched:\n";

                message += rpkgFilesNotPatched;

                MessageBoxShow(message);
            }
        }

        private void MessageBoxShow(string messageBoxString)
        {
            Message messageBox = new Message();

            messageBox.message.Content = messageBoxString;

            messageBox.ShowDialog();
        }

        Random rand;
        Dictionary<string, int[]> rpkgOffsets;
    }
}
